INSERT INTO `webprosjekt_vare` (`VNr`, `Varenavn`, `Pris`, `Beskrivelse`, `Bilde`, `KatNr`, `Antall`) VALUES
(1, 'Nikon D90 med AF-S DX 18-55mm VR, Norsk', 6295.00, 'Nikon D90 m/ DX 16-85 VR F3.5-5.6 <br />\r\n  <br />\r\nNikon D90: Unik speilrefleks med high definition videofilmfunksjon.<br />\r\nNyhet: Estimert tilgjengelig i oktober. Nikon D90 er et unikt speilrefleks med høy ytelse, god brukervennlighet og high definition (720P) filmfunksjon. Nikon D90 har en CMOS-bildesensor i DX-format med 12,3 megapiksler som gjør at utskrifter i store formater er fullt mulig. EXPEED-bildebehandlingssystemet er en avansert brikketeknologi i Nikon D90 kamera som gir deg som bruker enestående bildekvalitet på en enkel måte. Nikon D90 er et speilrefleks med med direkte visning av hva du skal fotografere på LCD skjermen. Med direkte visning kan du komponere og fotografere gjennom den 3-tommers LCD-skjermen, og ved hjelp av motivgjenkjenningssystemet og autofokus får du fanget inn bildene med en utrolig skarphet. Nikon D90 er det første speilrefleks kamera du kan spille inn filmer i JPEG-format med ved hjelp av D-Movie-funksjonen. Nikon D90 har hdmi kontakt for direktekobling til High Definition skjermer med høy oppløsning på visningen. Med hele 4,5 bilder i sekundet er det opp til fotografen og utvide den kreative horisonten innen fotografering. <br />\r\n<br />\r\nMedfølgende tilbehør: <br />\r\nOppladbart Li-ionbatteri EN-EL3e, Hurtiglader MH-18a, Lyd/video-kabel EG-D2, USB-kabel UC-E4, Øyemusling i gummi DK-21, Bærestropp AN-DC1, Kamerahusdeksel, Okulardeksel DK-5, Deksel for tilbehørssko BS-1, LCD-skjermdeksel BM-10, CD med Software Suite (ViewNX, Nikon Transfer, Apple QuickTime) <br />\r\n  <br />\r\nD90 forener teknologi fra Nikons fremste digitale speilreflekskameraer med en rekke sofistikerte funksjoner som hjelper deg til å oppnå fantastiske resultater. Ved hjelp av CMOS-bildesensoren i DX-format med 12,3 megapiksler og EXPEED-bildebehandlingssystemet kan du få enestående bildekvalitet med en rekke ulike ISO-følsomheter. Med modusen Direkte visning kan du komponere og fotografere gjennom den 3-tommers LCD-skjermen, og ved hjelp av motivgjenkjenningssystemet og autofokus får du fanget inn bildene med en utrolig skarphet. Du kan spille inn filmer i JPEG-format ved hjelp av D-Movie-funksjonen. Kameraets store bildesensor sikrer fantastisk filmkvalitet, og du kan skape dramatiske effekter ved å fotografere med en rekke ulike utbyttbare NIKKOR-objektiver, blant annet vidvinkel, makro og fiskeøye, eller ved å justere objektivets blenderåpning og eksperimentere med dybdeskarpheten. D90 er utformet for at du skal kunne dyrke din lidenskap for fotografering.', 'images/varebilder/WEB_Image Nikon D90 med AF-S DX 18-55mm VR Norsk 1158535835.jpeg', 1, 239),
(2, 'Canon EOS 1100D 15-55IS II', 4495.00, 'Avansert, kompakt og rimelig: Canon EOS 1100D gjør det enkelt å komme i gang med digitalt speilreflekskamera. <br />\r\n<br />\r\n- Suveren bildekvalitet med 12 megapiksler <br />\r\n- Funksjonsveiledning på skjermen <br />\r\n- Kreativ auto og Basic+ <br />\r\n- HD-videoopptak <br />\r\n- ISO-følsomhet på 100–6400 <br />\r\n- Bredt 9 punkts AF-system <br />\r\n- 63 soners iFCL-eksponeringsmåling <br />\r\n- LCD-skjerm på 6,8 cm (2,7 tommer) med 270 000 punkter <br />\r\n- Kan brukes med alle EF- og EF-S-objektiver', 'images/varebilder/WEB_Image Canon EOS 1100D 15-55IS II 12 Mpix 2.7 -959858850.jpeg', 1, 16),
(7, 'Fujifilm FinePix HS20 EXR', 3790.00, 'Fujifilm FinePix HS20 EXR  <br />\r\n  <br />\r\n– det ultimate alt-i-ett-kameraet <br />\r\n  <br />\r\nFinePix HS20 EXR er et bridgekamera for deg som vil ha systemkameraets egenskaper og bildekvalitet uten å behøve masse utstyr til en dyr penge. Full av godbiter som en ny EXR CMOS-sensor, høyhastighetsfotografering, enda bedre brukervennlighet, mange ulike videofunksjoner, pluss fantastiske 30x zoom og 16 mp oppløsning. FinePix HS20 vil bli en god etterfølger til suksessmodellen HS10. <br />\r\n  <br />\r\nFinePix HS20 EXR har en ny lys-sensitiv CMOS- sensor, i kombinasjon med velkjente EXR-prosessor. Fotografen kan velge eller kameraet velger automatisk mellom å prioritere høy oppløsning, best dynamisk omfang eller lavere oppløsning med høy ISO. Resultatet blir gode bilder under alle lysforhold. <br />\r\nFinePix HS20 EXR har nyutviklet bildestabilisator, rask seriefotografering og en stor klar 3” LCD skjerm og mulighet for å tilkoble TTL blits. <br />\r\n<br />\r\n- Ny 16 megapiksler EXR CMOS-sensor<br />\r\n- 30x optisk zoom som tilsvarer 24-720mm <br />\r\n- Ny 3.0" LCD med vektorbasert grafikk<br />\r\n- EXR teknologi<br />\r\n- Ny dynamiskt omfang på 1600% <br />\r\n- Høyhastighetsfotografering - stillbilde, 16 mp – 7,6 bilde/sek<br />\r\n- Høyhastighetsfotografering – film, 320 bilder /sek (320x100) <br />\r\n- Raw filformat<br />\r\n- Full HD-video med stereolyd i H.264-format<br />\r\n- Ny EXR Motivgjenkjenning med 27 motivprogrammer<br />\r\n- Filmsimulerings-alternativ (Velvia/Provia/Astia)<br />\r\n- 360° Motion Panorama-funksjon<br />\r\n- TTL blitssystem<br />\r\n- Fotobokhjelp', 'images/varebilder/Fujifilm FinePix HS20 EXR 30x vidvinkel 399698175.jpeg', 1, 14),
(12, 'Panasonic Lumix DMC-FZ45, 14Mp, 24x superzoom, 25mm vidvinkel, NO', 2895.00, 'Hybrid superzoom FZ45 – fra 25 mm vidvinkel til 24x optisk zoom for både bilder og HD-film. <br />\r\n  <br />\r\n25 mm vidvinkel 24x optisk zoom f/2,8–5.2 LEICA DC VARIO-ELMART-objektiv <br />\r\nHD Movie i AVCHD Lite og kreativ film-modus <br />\r\niA-modus (Intelligent Auto) med POWER O.I.S. (Optical Image Stabilizer) <br />\r\nIntelligent oppløsning <br />\r\n  <br />\r\n  <br />\r\n14,1 megapiksel DMC-FZ45 kommer med en 25 mm vidvinkel 24x optisk zoom f/2.8–5.2 LEICA DC VARIO-ELMART-objektiv. Det 25 mm vidvinkelobjektivet har ca. to ganger bildeområde sammenlignet med et standard 35 mm objektiv. Den kraftige 24x optiske zoomen kan brukes både til fotografering og videoopptak. Ekstra optisk zoom-funksjonen bruker midten av CCD til å forlenge 24x zoom-området til hele 50.6x zoom med en oppløsning på 3 megapiksel eller mindre i stillbilder. <br />\r\n  <br />\r\nDMC-FZ45 kan ta opp HD-film i AVCHD Lite med høy lydkvalitet fra Dolby Digital Stereo Creator. Motion JPEG-format er også tilgjengelig (1280 x 720 piksler). Bare trykk på Motion Picture-knappen for å starte innspillingen. I kreativ filmmodus kan lukkerhastigheten og blenderen justeres. iA-modus kan også aktiveres ved opptak av film. Når DMC-FZ45 er koblet til en Panasonic VIERA med en valgfri mini HDMI-kabel kan TV-ens fjernkontroll brukes til å spille av innholdet på en enklere måte. <br />\r\n  <br />\r\nPOWER O.I.S. (Optical Image Stabilizer) nesten dobler håndrystekorreksjonen i forhold til vanlige MEGA O.I.S. Dermed kan du ta vakre nattbilder uten at de blir uskarpe. Når du aktiverer funksjonene POWER O.I.S., intelligent ISO-kontroll, ansiktsgjenkjenning, AF/AE med ansiktsgjenkjenning, intelligent scenevelger, intelligent eksponering og AF-sporing, utfører kameraet alt arbeidet i iA-modus. Og Happy-modus optimerer fargegjengivelsen og lyset, noe som gjør minnerike motiver mer levende. <br />\r\n  <br />\r\nMed intelligent oppløsningsteknologi vil konturer, detaljerte teksturområder og bløtgraderingsområder oppdages automatisk. I tilegg til generelt større skarphet, vil denne teknologien signalbehandle piksel for piksel på den mest effektive måten i forhold til det enkelte området. Resultatet er at både stillbilder og videoopptak blir naturlig klare og skarpe. Den intelligente zoomen utvider zoom-området med ca. 1,3x og beholder bildekvaliteten selv i kombinasjon med digitalzoomen. 24x optisk kan i realiteten forlenges til hva som tilsvarer 32x <br />\r\n  <br />\r\nFordelen med DMC-FZ45 er den glimrende manuelle kontrollmuligheten. Styrehjulet gjør kameraet mer brukervennlig. I tillegg er det mulig å betjene DMC-FZ45 manuelt på en enkel måte. Min farge-modus og film-modus gjør det enklere å få fargeeffektene til å bli slik som du vil. Avansert scenemodus lar deg raskt velge ofte brukte moduser som portrett, landskap, sport, nattportrett eller nærmodus med modushjulet. DMC-FZ45 har en 3,0 tommers LCD-skjerm med 230,000 punkters oppløsning og bred visningsvinkel.', 'images/varebilder/WEB_Image Panasonic Lumix DMC-FZ45 14Mp 24x super1884984334.Jpeg', 2, 86),
(13, 'Canon IXUS 1000HS Sølv, Norsk', 2490.00, '10x optisk zoom med bildestabilisator<br />\r\nEt ultrakompakt 10x zoomobjektiv med avansert optisk bildestabilisator gjør det mulig å fotografere med lengre brennvidde og i lite lys uten å bruke stativ.<br />\r\n <br />\r\nKompakt og stilig kamerahus i metall<br />\r\nElegant kamera i lommeformat med moderne IXUS-design i metall og superzoomobjektiv som kan trekkes helt inn.<br />\r\n <br />\r\nHS System med 10,0 megapikslers CMOS-sensor med høy følsomhet<br />\r\nHS System gir imponerende bildekvalitet i alle lysforhold. 10,0 megapikslers CMOS-sensor med høy følsomhet åpner for raske serieopptak og spesielle høyhastighetsfunksjoner, som Nattbilder uten stativ og Superlangsom film. <br />\r\n<br />\r\nFull HD-film med dynamisk bildestabilisator<br />\r\nFang de beste øyeblikkene i full HD (1080p) med stereolyd, og koble kameraet til en HDTV ved hjelp av HDMI-porten, og del opplevelsene med venner. Følg motivet med kameraets 10x optiske zoom. Takket være den dynamiske bildestabilisatoren får du stødigere og skarpere film selv om du gjør opptak mens du går.<br />\r\n <br />\r\nStor PureColor II G LCD-skjerm på 7,6 cm (3,0")<br />\r\nDenne modellen har en PureColor II G LCD-skjerm med herdet glass som gir ekstra beskyttelse, bedre kontrast og vid visningsvinkel.<br />\r\n <br />\r\nSuperlangsom film og høyhastighetsopptak<br />\r\nMed innstillingen Superlangsom film tas det opp 240 bilder i sekundet for saktere opptak av fartsfylt action som sport og dyreliv, mens høyhastighetsserier sørger for at du får med alt som skjer, med kontinuerlig opptak av 8,8 bilder i sekundet (2,5 megapiksler) til minnekortet er fullt.<br />\r\n <br />\r\nSmart Auto<br />\r\nKonsentrer deg om bildekomposisjonen, og la kameraet ta seg av resten. Smart Auto-programmet bruker en rekke Canon-teknologier til å gjenkjenne ulike motiver i forskjellige opptaksforhold og velger automatisk de beste innstillingene blant 28 tilgjengelige alternativer.<br />\r\n <br />\r\nMange opptaksprogrammer, inkludert Beste bilde<br />\r\nProgrammet Beste bilde tar en høyhastighetsserie på fem bilder og velger automatisk ut det beste av disse og lagrer det. Med programmet Nattbilder uten stativ kan du fotografere motiver med lite lys uten å bruke stativ.<br />\r\n <br />\r\nSmart lukker med blunkeselvutløser<br />\r\nSmart lukker-programmet har tre praktiske selvutløserfunksjoner, inkludert blunkeselvutløser som tar bilde to sekunder etter at kameraet registrerer at noen blunker.<br />\r\n <br />\r\nTips og triks<br />\r\nDe fleste alternativene i opptaks- og avspillingsmenyene har en kort forklaring, slik at du kan lære om kamerafunksjonene og øke kreativiteten.', 'images/varebilder/WEB_Image Canon IXUS 1000HS Solv  Norsk 10 Mpix  1512645197.jpeg', 3, 71),
(14, 'Casio EXILIM EX-FH100 Sort Norsk', 2275.00, 'EX-FH100 er et elegant designet kamerahus som har flere høyhastighetsfunksjoner. F.eks High Speed- seriebildeopptak med opp til 40 bilder per sekund, eller High Speed- videoinnspilling med opp til 1000 bilder per sekund.<br />\r\nDen innovative bakgrunnsbelyste CMOS High Speed- bildesensoren med 10,1 megapixler gir skarphet og detaljerte bilder med fantastiske farger. Dette elegante høyhastighetskamera har 10x optisk zoom med mekanisk bildestabilisering og et 24 mm vidvinkelobjektiv.<br />\r\n <br />\r\nCMOS-sensor (oppløsning)<br />\r\nOppløsningen er på 10,1 millioner effektive pixler med en bildesensor som er 1/2,3 tum stor.<br />\r\n <br />\r\nOptik<br />\r\n10 x optisk zoom med en brennvidde på 4,3 - 43 mm som tilsvarer ca 24-240 mm i småbildeformatet.<br />\r\nStørste blender ligger mellom f/3,2-f/5,7<br />\r\nnærgrense 7 cm.<br />\r\n <br />\r\nFunksjoner<br />\r\n<br />\r\n-Kameraet har automatiske eksponeringsinnstillinger,og forhåndsprogrammerte motivprogram<br />\r\n-Lukkertiden kan stilles mellom 1-1/12000 s.<br />\r\n-ISO-verdien kan stilles mellom 100-3200.<br />\r\n-Eksponeringskompensasjon kan gjøres med +-2EV i steg om 1/3EV<br />\r\n-LCD-displayet er på 3,0 tommer med 230 400 bildepunkter.<br />\r\n-Filmsekvenser kan spilles in i HD-uppløsningen 1280 x 720 med 30 bilder per sekund<br />\r\n-Selvutløseren kan stilles til 2, 10 s eller eget valg<br />\r\n-Overføring til datamaskin USB 2.0.<br />\r\n-Manuelle innstillinger på blender og lukkertid<br />\r\n-Med high speed seriebildeopptak kan du ta opp til 40 bilder per sekund<br />\r\n-Den mekaniske bildestabilisatoren reduserer bevegelsesuskarphet.<br />\r\n<br />\r\n <br />\r\nLagring<br />\r\nFilene lagres på SD/SDHC-minnekort.<br />\r\n <br />\r\nLeveres med :<br />\r\nCasio Exilim EX-FH100,oppladbart batteri NP-90, batterilader, nettadapter,USB-kabel,<br />\r\nAV-kabel, kamerarem, CD-ROM', 'images/varebilder/WEB_Image Casio EXILIM EX-FH100 Sort Norsk.Jpeg', 3, 6),
(15, 'Kodak EasyShare M320 Rød', 395.00, 'Lekkert og kompakt digitalkamera med 3xoptisk zoom og 9,2 megapikslers oppløsning!<br />\r\n <br />\r\n9.2 MP for prints up to 30 × 40 in. (76 × 102 cm)<br />\r\n3X optical zoom<br />\r\nKODAK PERFECT TOUCH Technology<br />\r\nLi-Ion rechargeable battery and AC adapter for charging included<br />\r\nHD picture capture<br />\r\n <br />\r\nStandard features<br />\r\nSensor type  1 / 2.5 in. CCD<br />\r\nCCD total pixels  9.3 MP (3512 × 2640)<br />\r\nEffective pixels  9.2 MP (3496 × 2626)<br />\r\nLens  KODAK AF 3X Optical Aspheric Lens, 34–102 mm (35 mm equiv.), f/2.8–5.1<br />\r\nLens protection  built-in mechanical lens cover<br />\r\nZoom  3X optical, 5X digital', 'images/varebilder/WEB_Image Kodak EasyShare M320 Rod 9.2 MP  3x opti1283254084.Jpeg', 3, 22),
(16, 'Nikon Coolpix S8000 Sølv', 1445.00, 'Verden''s tynneste* 10x zoomkamera med vidvinkelfunksjon. NIKKOR’s ED-glasslinse sørger for skarpe resultater enten du tar stillbilder eller tar opp HD-filmklipp, og 10x zoom gir deg uovertruffen komposjonsfrihet slik at du kan lage alt fra imponerende vidvinkelbilder og telefotobilder, til detaljerte nærbilder i makromodus. Det avanserte blitsstyringssystemet gir mer naturlige resultater når du tar bilder under dårlige lysforhold, og den svært klare LCD-skjermen på 7,5 cm (3 tommer) med høy oppløsning forbedrer både opptaket og avspillingen med et minimum av refleksjon, også i skarpt sollys. Med et kamerahus som gir en uovertruffen bærbarhet, er COOLPIX S8000 en stilig måte å være kreativ på når det gjelder hvordan du tar bilder av verden.<br />\r\n <br />\r\n <br />\r\n<br />\r\n-Dimensjoner: (BxDxH) 10.3 cm x 2.7 cm x 5.7 cm<br />\r\n-Vekt: 183 g<br />\r\n-Farge: Sølv<br />\r\n-Flashminnestøtte SD Memory Card, SDHC Memory Card<br />\r\n-Integrert minne: 32 MB<br />\r\n-Opptaksmodi Bildefilmmodus<br />\r\n-Bildepunkter/oppløsning: 14.2 megapiksel<br />\r\n-Linsesystem: 10 x zoom-linse - 5.4 mm - 54 mm - f/3.5-5.6<br />\r\n-Fokusjustering: Automatisk<br />\r\n-Nærgrense: 50 cm<br />\r\n-Digital Zoom 2 x<br />\r\n-Bildestabilisator: Optisk<br />\r\n-Kamerablitz: Popup-blitz<br />\r\n-Røde øyne-reduksjon: Ja<br />\r\n-Mikrofon: Mikrofon - innebygget - electret-kondensator - stereo<br />\r\n-Skjerm: LCD-skjerm - TFT aktiv matrise - 3" - farge<br />\r\n-Støttet batteri: 1 x Li-ion oppladbart batteri EN-EL12 ( inkludert )<br />\r\n', 'images/varebilder/WEB_Image Nikon Coolpix S8000 Solv.Jpeg', 3, 158),
(17, 'Panasonic DMC-TZ18 Sort, Norsk', 2495.00, 'Ultrakompakt kamera med en kraftig 16x optisk zoom<br />\r\n<br />\r\n-LEICA DC-objektiv med 16x optisk zoom fra en 24 mm ultra-vidvinkel.<br />\r\n-20x intelligent zoom med intelligent oppløsningsteknologi.<br />\r\n-iA-modus (intelligent auto) med POWER O.I.S. (optisk bildestabilisering).<br />\r\n-HD-filmopptak og LUMIX-bildeopplasting.<br />\r\n-Stor, 3-tommers intelligent LCD-skjerm med 230 000-prikkers oppløsning.<br />\r\n-Enkel, manuell kontroll med A/S/M-modus.<br />\r\n <br />\r\nDMC-TZ18 har 14,1 megapiksler og et LEICA DC-objektiv med 24 mm ultra vidvinkel og 16x optisk zoom (35 mm kamera tilsvarer: 24-384 mm), inkludert Nano Surface Coating-teknologi som dramatisk reduserer ekkobilder og gjenskinn. Den optiske zoomen på 16x kan brukes både til å ta bilder og til filmopptak. Vidvinkelobjektivet på 24 mm har en visning som er omtrent 213 % større sammenlignet med et vanlig 35 mm objektiv.<br />\r\n <br />\r\nMed intelligent oppløsningsteknologi oppdages automatisk 3 områder, konturer, detaljerte områder og myke fargeoverganger. Bortsett fra en helhetlig bedring av skarpheten, utfører denne teknologien en presis signalbehandling piksel etter piksel på en mest mulig effektiv måte, avhengig av området. Resultater er klare og skarpe bilder. Det intelligente zoomforholdet er omtrent 1,3x og beholder bildekvaliteten, selv når den er kombinert med en digital zoom. 16x optisk kan virtuelt utvides til tilsvarende 20x.<br />\r\n <br />\r\nMed POWER O.I.S. får du en nesten dobbelt så stor korreksjon av ustø hender som med en konvensjonell MEGA O.I.S. Dermed kan du ta skarpe, klare zoombilder uten uskarpheter. Når kameraet er i iA-modus, aktiveres POWER O.I.S., intelligent ISO-kontroll, ansiktsgjenkjennelse AF/AE, intelligent scenevelger med solnedgangmodus, intelligent eksponering og AF Tracking. Glad-modusen optimerer fargemetningen og lysstyrken for livaktige, minneverdige scener.<br />\r\n <br />\r\nDMC-TZ18 kan ta opp filmer i høy definisjon (1280 x 720 piksler med 30 fps i Motion JPEG-format). Den kraftige 16x optiske zoomen er tilgjengelig for filmopptak. DMC-TZ18 kan også laste opp filmer på YouTube og bilder på Facebook. Alt du trenger å gjøre er å merke bilder som du vil dele og sette inn SD/SDHC/SDXC-minnekortet i PC-en din. Opplastingen starter automatisk ved å følge veiledningen. Når det er så enkelt, kan du dele bilder og filmer med familie og venner over hele verden.<br />\r\n <br />\r\nDMC-TZ18 har en stor 3-tommers intelligent LCD-skjerm. Den justerer automatisk skjermens lysstyrke idet det omkringliggende lysnivået endrer seg. Den store skjermen reflekterer fine detaljer, slik at det blir lett å sjekke fokuset før du tar bilder. Den brukes også til visning i lite format av bilder og videoer, slik at du og vennene dine kan kose dere med å se på bilder sammen. DMC-TZ18 har manuelle kontroller som er lette å bruke. Blenderåpning-prioriteringen skaper en bakgrunnseffekt med mykt fokus og lukker-prioriteringen produserer en dynamisk gjengivelse av rennende vann.', 'images/varebilder/WEB_Image Panasonic DMC-TZ18 Sort  Norsk 14 1Mp  1-19844135.Jpeg', 3, 29),
(18, 'Pentax Optio RS1000 Sort', 1135.00, 'Endre kameraets utseende - design din egen front!<br />\r\nOptio RS1000 har en oppløsning på 14 megapixler, en tydelig 3" LCD-skjerm og 4x optisk zoom med 27,5 mm vidvinkel, samt videoinnspilling i HD-format. Kamerahuset er i kredittkortformat, noe som gjør kameraet til en fast følgesvenn. En unik egenskap er muligheten til å endre kamerans utseende med designerarkpå kameraets front. Man kan også lage sitt eget design basert på fotografier og illuastrasjoner.<br />\r\n <br />\r\n<br />\r\n-14 Megapixel CCD sensor<br />\r\n-4x Optisk zoom med vidvinkel (tilsvarer 27,5-110mm)<br />\r\n-Filmer i HD-oppløsning (1280x720, 30fps)<br />\r\n-Videoredigering direkte i kameraet<br />\r\n-ISO-følsomhet: 100 - 6400<br />\r\n-3" LCD display, 230.000 punkter<br />\r\n-Ansikts- og smilegjenkjenning<br />\r\n-Oppladbart Lithium-ion batteri (PENTAX D-LI08)<br />\r\n-Kortspalte for SD/SDHC<br />\r\n-Utganger: USB 2.0, AV<br />\r\n-Størrelse: 92x56x20,5mm (inkludert linsens dybde)<br />\r\n-Vekt inkl. batteri og minnekort: 130g<br />\r\n-Vekt: 113g<br />\r\n-Medfølgende software: Arcsoft Media Impression 2.0 for PENTAX<br />\r\n<br />\r\n <br />\r\n<br />\r\n    Endre kameraets utseende - design din egen front!<br />\r\n    Tilgjengelig i to ulike farger: Hvit og Sort<br />\r\n    Tre ytterligere designerark medfølger hver farge<br />\r\n    Sett med 5 forhåndslagde 10x15 designerark medfølger for utskrift av egne fronter<br />\r\n    Enkelt verktøy for å bytte fronter medfølger<br />\r\n', 'images/varebilder/WEB_Image Pentax Optio RS1000 Sort 14mp  4x optisk143254862.Jpeg', 3, 14),
(19, 'Canon Powershot S95 digitalkamera', 3295.00, 'HS System, f/2-objektiv og 10,0 megapiksler sørger for at PowerShot S95 leverer førsteklasses resultat i lite lys. HD-film, RAW-opptak og unik kontrollring på objektivet for manuell kontroll gjør dette til et perfekt lommekamera for seriøse fotografer.<br />\r\n <br />\r\n <br />\r\nEgenskaper:<br />\r\n-HS System og f/2-objektiv<br />\r\n-CCD med høy følsomhet og 10,0 megapiksler<br />\r\n-28mm vidvinkelobjektiv, 3,8x zoom og hybrid bildestabilisator<br />\r\n-Kontrollring på objektivet, fullstendig manuell kontroll og RAW-opptak<br />\r\n-7,5 cm (3,0") PureColor II G LCD-skjerm<br />\r\n-HD-filmer, HDMI<br />\r\n-Program for stort dynamisk omfang<br />\r\n-Smart Auto<br />\r\n-Fotografering i flere formater<br />\r\n-Vanntett kamerahus som tilleggsutstyr<br />\r\n<br />\r\nLommekameraet for den seriøse fotografen<br />\r\n <br />\r\nHS System med 10,0 megapikslers CCD-sensor med høy følsomhet<br />\r\nMuligheten til å fotografere med høyere ISO-hastigheter og likevel beholde førsteklasses bildekvalitet er selve nøkkelfunksjonen på PowerShot S95. HS System er en kraftfull kombinasjon av en sensor med høy følsomhet og den siste DIGIC 4-bildeprosessoren som gir enestående bildekvalitet i en rekke lysforhold, også i svakt lys.<br />\r\n <br />\r\nF/2.0, 28mm vidvinkelobjektiv med 3,8x zoom og hybrid bildestabilisator<br />\r\nLyssterkt f/2.0-objektiv åpner for fotografering uten stativ eller blits i omgivelser med lite lys. Den optiske bildestabilisatoren korrigerer kamerarystelser slik at du kan bruke opptil 4 trinn lengre lukkertid, mens den hybride bildestabilisatoren sørger for skarpere resultat ved makrofotografering.<br />\r\n <br />\r\nKontrollring på objektivet, fullstendig manuell kontroll og RAW-opptak<br />\r\nKontrollringen på objektivet gir rask tilgang til manuelle justeringer. Du kan styre ulike funksjoner med kontrollringen, for eksempel zoom, blenderåpning, lukkertid, fokus, ISO og eksponeringskompensasjon. Du kan også ta RAW-bilder som du deretter kan etterbehandle i programmet Digital Photo Professional som følger med kameraet.<br />\r\n <br />\r\n7,5 cm (3,0") PureColor II G LCD-skjerm<br />\r\nLyssterk og klar LCD-skjerm med et lag av herdet glass som gir ekstra sterk overflate og bedre synlighet når du fotograferer i sterkt lys.<br />\r\n <br />\r\nHD-filmer, HDMI<br />\r\nKameraet har HD-filmopptak med 720p og 24 bilder/sek. Du kan ta opp HD-film med stereolyd og spille av filmen via HDMI-kontakten.<br />\r\n <br />\r\nProgram for stort dynamisk omfang<br />\r\nI motiver med høy kontrast kan tradisjonelle digitalkameraer ha problemer med få med alle detaljer i mørke og lyse områder. Når kameraet brukes med stativ, tar programmet Stort dynamisk omfang flere eksponeringer av samme motiv og setter dem sammen i kameraet slik at du får med detaljene i et kontrastrike motiver.<br />\r\n <br />\r\nSmart Auto<br />\r\nSmart Auto-programmet bruker teknologien for motivgjenkjenning til å bestemme motivet blant 28 tilgjengelige alternativer og sørger for optimalt resultat. Smart blitseksponering styrer blitsstyrken og -bruken og sikrer naturlige bilder.<br />\r\n <br />\r\nFotografering i flere formater<br />\r\nDu kan ta opp i en rekke formater, blant annet 3:2, 1:1, 16:9 og 4:5. Det gir perfekt komposisjon på opptakstidspunktet uten behov for redigering i etterkant.', 'images/varebilder/WEB_Image Canon Powershot S95 digitalkamera.Jpeg', 3, 44),
(20, 'Casio EXILIM EX-G1 Rød', 2290.00, 'For opplevelser uten grenser<br />\r\nEXILIM G EX-G1 tåler også tøffe situasjoner – uansett hvilke opplevelser som dukker opp. G-utformingen for absorbering av støt beskytter kameraet mot riper og støt. Fuktighet og støv må ganske enkelt holdes ute. Under det robuste dekselet finner du 12 megapiksler, 3 x optisk zoom og mange andre funksjoner. EXILIM G EX-G1 er robust i enhver situasjon – takket være den svært flate og elegante utformingen.<br />\r\n <br />\r\nStøtsikkert<br />\r\nFor tøffe øyeblikk  <br />\r\nEX-G1 mestrer utfordringene uten skader – enten det gjelder klatring, terrengsykling eller snøbrettkjøring. Overflaten på det robuste og støtsikre kameraet gir godt grep, derfor kan det også betjenes med hansker. Hvis du noen gang mister grepet på kameraet, er det ingen fare. Kameraet tåler en fallhøyde på opptil 2,13 meter. Hvis kameraet skal utsettes for større høyder, kan det sikres med en karabinkrok. <br />\r\nVanntett og støvbeskyttet<br />\r\nSvært slitesterkt  <br />\r\nEXILIM G EX-G1 tåler alle slags aktiviteter – fra små utendørsopplevelser til store oppdagelser. Du får også med deg imponerende bilder hjem. Det robuste kameraet trosser de tøffeste forholdene: Det er beskyttet mot støv, sand, regn og snø, og er også vanntett på opptil 3 m dybde. Ideelle forutsetninger for sportslig innsats utendørs.<br />\r\n <br />\r\nUltraflat G-utforming<br />\r\nStørrelsen er avgjørende  <br />\r\nEX-G1 er også i sitt rette element i storbymylderet. Kameraet passer i alle slags lommer og vesker. Det er enkelt å fiske frem og like enkelt å legge vekk. Huset er bare 19.9 millimeter tynt. Det elegante kameraet er også i toppsjiktet når det gjelder utforming. Det elegante huset i G-utforming tar ikke bare pusten fra deg – det inneholder også mange intelligente funksjoner.<br />\r\n <br />\r\n300 bilder med én batteriladning<br />\r\nAlltid klart til bruk  <br />\r\nHvis batteriet plutselig er utladet når du ser et perfekt motiv, er det svært ergerlig – men det kan unngås. Kombinasjonen av en energibesparende prosessor og et svært kraftig litiumion-batteri sørger for at EXILIM G EX-G1 har nok strøm på lange, opplevelsesrike dager, og at du kan ta opptil 300 bilder iht. CIPA-standard med én batterilading.<br />\r\n <br />\r\n12,1 megapiksler<br />\r\nBriljant toppytelse  <br />\r\nIkke glem at CCD-brikken til EX-G1 gir briljante 12,1 megapiksler. Fordel: Kameraet gir knivskarpe og svært detaljrike bilder. Du kan både forstørre detaljene og lage bilder i plakatstørrelse med fremragende kvalitet uten at bildeinformasjon går tapt.<br />\r\n6,4 cm (2,5 tommers) TFT-fargeskjerm<br />\r\nNye perspektiver  <br />\r\nDen store 6,4 cm (2,5 tommer) TFT-fargeskjermen med høy oppløsning gjør utformingen til EXILIM G EX-G1 fullkommen: Den dekker nesten hele kameraets bakside, slik at du kan se nøye på motivene, lagrede bilder eller filmer – i sollys eller i mørket.<br />\r\n <br />\r\n Intelligent AF-funksjon<br />\r\nAutomatisk skarphet  <br />\r\nAldri flere uskarpe motiver i bildene dine! Den intelligente AF-funksjonen registrerer automatisk de ideelle fokusområdene og stiller disse inn skarpt. Når utløseren er trykt halvveis ned, gjenkjenner den bakenforliggende teknologien størrelsen og konturene til motiver, og velger deretter automatisk optimal fokusering. På den måten kan du ganske avslappet vente på det riktige øyeblikket, og trenger bare å trykke på utløseren.<br />\r\nVarious Flash<br />\r\nBlits til enhver situasjon  <br />\r\nDu kan tilpasse blitsen på EXILIM G EX-G1 til ulike situasjoner. Følgende forhåndsinnstillinger er tilgjengelige:<br />\r\n <br />\r\n• Reduksjon av røde øyne: Reduserer "røde øyne-effekten".<br />\r\n• Myk blits: For ideell belysning av objekter i umiddelbar nærhet.<br />\r\n• Auto: Blitsen slås automatisk på etter behov.<br />\r\n• På: Blitsen er alltid slått på.<br />\r\n• Av: Blitsen er alltid slått av. <br />\r\nAV-kontakt<br />\r\nTV-show i stedet for bildeframvisning  <br />\r\nInviter venner og kjente til å se de beste filmene og bildene dine i stort format. Du kobler bare EXILIM G EX-G1 til TVen ved hjelp av AV-uttaket.<br />\r\n <br />\r\nMinnekortspor<br />\r\nSom du ønsker  <br />\r\nEX-G1 kan lagre bildene og filmene på microSD- eller microSDHC-kort. Hvilket av disse alternative kortene du vil bruke, kan du avgjøre spontant og fleksibelt.', 'images/varebilder/WEB_Image Casio EXILIM EX-G1 Rod 12 mpix  3x zoom -1687613819.Jpeg', 4, 3),
(21, 'Panasonic DMC-FT3 Blå, Norsk', 3395.00, 'Et svært solid kamera med full HD-filmopptak og forskjellige funksjoner for utendørs bruk.<br />\r\n<br />\r\n-Vanntett 12 m, tåler temperaturer ned til -10 ?, tåler fall på opptil 2 m, samt støv.<br />\r\n-1920 x 1080 full HD-film i AVCHD-format og POWER O.I.S. med aktiv modus.<br />\r\n-Innebygd GPS-funksjon for rask og enkel gjenfinning.<br />\r\n-Stedsinformasjon med høydemåler, kompass og barometer.<br />\r\n-3D-fotomodus og iA-modus med POWER O.I.S. (optisk bildestabilisator).<br />\r\n-28 mm vidvinkel LEICA DC-objektiv og 12,1-megapiksel høyhastighets CCD.<br />\r\n<br />\r\nDMC-FT3 er designet for aktiv bruk, den er vanntett ned til et dyp på 12 meter, tåler kulde ned til -10 ?, tåler støt fra fall på opptil 2 meter og tåler støv. I filmopptak skrus LED-lyset seg på for å få vakre filmopptak under vann eller ved leirplassen om kvelden.<br />\r\n <br />\r\nDMC-FT3 kan ta opp filmer i full HD 1920 x 1080 i AVCHD-format med Dolby® Digital Creator. Med en tilhørende knapp kan du øyeblikkelig begynne å filme uten at du trenger noen ekstra innstillinger. iA-modus er tilgjengelig i filmopptak. POWER O.I.S. med aktiv modus er nyttig når du tar bilder mens du går eller leker med barn. Både fotografier og filmer i AVCHD-format som er tatt opp på et SD-minnekort er lette å se på på en Panasonic VIERA-TV.<br />\r\n <br />\r\nDMC-FT3 har en innebygd GPS-enhet. Lokaliseringsdataene for hvor bildet ble tatt med lengde- og breddegrad legges inn automatisk i EXIF-dataene. Disse geotaggede bildene kan vises på kart på sosiale medier på Internett. DMC-FT3 viser også navnet på fylket og stedet eller byen, samt landemerker ved bruk av interne data for å gi deg informasjon i sanntid om stedet når du fotograferer. Bilder med stedsnavnene blir automatisk sortert alfabetisk i en virtuell mappe i kameraet, slik at det er lett å søke.<br />\r\n <br />\r\nDMC-FT3 bruker kompass, høydemåler og barometer for å vise og lagre høyde, barometertrykk og orienteringsdata. Barometeret virker også som en indikator på trykk under vann. Med en solid design, GPS og avanserte måleinstrumenter tilbyr den mye moro i mange forskjellige situasjoner. Ta med kameraet på fisketur eller camping om våren, på dykking, snorkling, surfing om sommeren, fjellklatring om høsten eller snøbrettkjøring om vinteren! DMC-FT3 kan brukes aktivt i naturen hele året.<br />\r\n <br />\r\n3D-fotomodusen skaper et 3D-bilde i kameraet. Ta 20 panoreringsbilder, så velger kameraet automatisk de to bildene som passer best og sammenfører dem til en 3D-sammensetning. 3D-bildet kan vises i 3D via 3D-bildeviseren på en Panasonic VIERA-TV og gir deg en helt ny, spennende opplevelse. I iA-modus forhindrer anti-uskarphetsmodus og POWER O.I.S. uskarpheter fra både motiver i bevegelse og ustø hender. Når du har valgt iA-modus, virker oppdagelses- og korreksjonsfunksjonene for å ta bilder på en enkel måte.', 'images/varebilder/WEB_Image Panasonic DMC-FT3 Bla  Norsk 12 1Mp  4 6-956682954.Jpeg', 4, 39),
(22, 'Panasonic DMC-FT3 Orange, Norsk', 3395.00, 'Et svært solid kamera med full HD-filmopptak og forskjellige funksjoner for utendørs bruk.<br />\r\n<br />\r\n-Vanntett 12 m, tåler temperaturer ned til -10 ?, tåler fall på opptil 2 m, samt støv.<br />\r\n-1920 x 1080 full HD-film i AVCHD-format og POWER O.I.S. med aktiv modus.<br />\r\n-Innebygd GPS-funksjon for rask og enkel gjenfinning.<br />\r\n-Stedsinformasjon med høydemåler, kompass og barometer.<br />\r\n-3D-fotomodus og iA-modus med POWER O.I.S. (optisk bildestabilisator).<br />\r\n-28 mm vidvinkel LEICA DC-objektiv og 12,1-megapiksel høyhastighets CCD.<br />\r\n<br />\r\n <br />\r\n <br />\r\nDMC-FT3 er designet for aktiv bruk, den er vanntett ned til et dyp på 12 meter, tåler kulde ned til -10 ?, tåler støt fra fall på opptil 2 meter og tåler støv. I filmopptak skrus LED-lyset seg på for å få vakre filmopptak under vann eller ved leirplassen om kvelden.<br />\r\n <br />\r\nDMC-FT3 kan ta opp filmer i full HD 1920 x 1080 i AVCHD-format med Dolby® Digital Creator. Med en tilhørende knapp kan du øyeblikkelig begynne å filme uten at du trenger noen ekstra innstillinger. iA-modus er tilgjengelig i filmopptak. POWER O.I.S. med aktiv modus er nyttig når du tar bilder mens du går eller leker med barn. Både fotografier og filmer i AVCHD-format som er tatt opp på et SD-minnekort er lette å se på på en Panasonic VIERA-TV.<br />\r\n <br />\r\nDMC-FT3 har en innebygd GPS-enhet. Lokaliseringsdataene for hvor bildet ble tatt med lengde- og breddegrad legges inn automatisk i EXIF-dataene. Disse geotaggede bildene kan vises på kart på sosiale medier på Internett. DMC-FT3 viser også navnet på fylket og stedet eller byen, samt landemerker ved bruk av interne data for å gi deg informasjon i sanntid om stedet når du fotograferer. Bilder med stedsnavnene blir automatisk sortert alfabetisk i en virtuell mappe i kameraet, slik at det er lett å søke.<br />\r\n <br />\r\nDMC-FT3 bruker kompass, høydemåler og barometer for å vise o